import { initializeApp } from "firebase/app";
import {
  getAuth,
  GoogleAuthProvider,
  GithubAuthProvider,
  signInWithPopup,
  signOut,
  setPersistence,
  browserSessionPersistence
} from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyB6V18h4nBa9rsLpmabIgbmTESV6sLa53s",
  authDomain: "nombrado-auth-login.firebaseapp.com",
  projectId: "nombrado-auth-login",
  storageBucket: "nombrado-auth-login.firebasestorage.app",
  messagingSenderId: "410135861377",
  appId: "1:410135861377:web:5f312946c453c6968b9580",
  measurementId: "G-45ST4K1V8K"
};

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);

// Set authentication persistence to session (cleared when browser closes)
setPersistence(auth, browserSessionPersistence);

export const googleProvider = new GoogleAuthProvider();
export const githubProvider = new GithubAuthProvider();

// Optionally request extra scopes from GitHub
// githubProvider.addScope('read:user');

export async function signInWithGoogle() {
  return signInWithPopup(auth, googleProvider);
}

export async function signInWithGithub() {
  return signInWithPopup(auth, githubProvider);
}

export async function signOutUser() {
  return signOut(auth);
}
