import React from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useTheme } from "../context/ThemeContext";

function Profile() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { theme, toggleTheme } = useTheme();

  console.log("Profile user:", user);

  return (
    <div className="min-h-screen bg-white flex items-center justify-center p-4">
      <div className="max-w-sm w-full text-center">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-light text-gray-900">Profile</h1>
          <button
            onClick={toggleTheme}
            className="ml-2 px-3 py-1 rounded bg-gray-100 text-sm text-gray-800"
            aria-label="Toggle theme"
          >
            {theme === "dark" ? "Light" : "Dark"}
          </button>
        </div>
        {user ? (
          <div className="space-y-4">
            <div className="flex justify-center">
              <img
                src={
                  user.photoURL ||
                  `https://ui-avatars.com/api/?name=${encodeURIComponent(
                    user.name || user.email || "User"
                  )}&background=ddd&color=555&size=128`
                }
                alt="Profile"
                className="w-24 h-24 rounded-full object-cover mb-2"
              />
            </div>
            <div className="bg-gray-50 p-4 rounded shadow">
              <p className="text-gray-700"><strong>Name:</strong> {user.name}</p>
              <p className="text-gray-700"><strong>Email:</strong> {user.email}</p>
            </div>
            <button
              onClick={() => navigate('/')}
              className="w-full bg-blue-500 text-white py-3 px-4 font-medium hover:bg-blue-600 transition duration-300 rounded"
            >
              Back to Home
            </button>
          </div>
        ) : (
          <p className="text-gray-600">Please log in to view your profile.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
