import React from "react";
import { useAuth } from "../context/AuthContext";
import { useTheme } from "../context/ThemeContext";

function Profile() {
  const { user } = useAuth();
  const { theme, toggleTheme } = useTheme();

  console.log("Profile user:", user);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 flex items-center justify-center p-4">
      <div className="max-w-sm w-full text-center">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-light text-gray-900 dark:text-gray-100">Profile</h1>
          <button
            onClick={toggleTheme}
            className="ml-2 px-3 py-1 rounded bg-gray-100 dark:bg-gray-800 text-sm text-gray-800 dark:text-gray-200"
            aria-label="Toggle theme"
          >
            {theme === "dark" ? "Light" : "Dark"}
          </button>
        </div>
        {user ? (
          <div className="space-y-4">
            <div className="flex justify-center">
              <img
                src={
                  user.photoURL ||
                  `https://ui-avatars.com/api/?name=${encodeURIComponent(
                    user.name || user.email || "User"
                  )}&background=ddd&color=555&size=128`
                }
                alt="Profile"
                className="w-24 h-24 rounded-full object-cover mb-2"
              />
            </div>
            <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded">
              <p className="text-gray-700 dark:text-gray-200"><strong>Name:</strong> {user.name}</p>
              <p className="text-gray-700 dark:text-gray-200"><strong>Email:</strong> {user.email}</p>
            </div>
            <button
              onClick={() => window.location.href = '/'}
              className="w-full bg-black text-white py-3 px-4 font-medium hover:bg-gray-800 transition duration-300"
            >
              Back to Home
            </button>
          </div>
        ) : (
          <p className="text-gray-600 dark:text-gray-300">Please log in to view your profile.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
